package com.example.wealthwise.Controller.Client;

import com.example.wealthwise.Models.Client;
import com.example.wealthwise.Models.Model;
import com.example.wealthwise.Models.Transaction;
import javafx.fxml.Initializable;
import javafx.scene.control.Label;
import javafx.scene.image.Image;
import javafx.scene.image.ImageView;

import java.net.URL;
import java.time.format.DateTimeFormatter;
import java.util.ResourceBundle;

public class TransactionCellController implements Initializable {

    public Label category_lbl;
    public Label amount_lbl;
    public Label receiver_lbl;
    public Label sender_lbl;
    public Label trans_date_lbl;
    public ImageView in_icon;

    private final Transaction transaction;

    public TransactionCellController(Transaction transaction){
        this.transaction = transaction;
    }

    @Override
    public void initialize(URL url, ResourceBundle resourceBundle) {
        if (transaction == null) return;

        // Set basic fields
        category_lbl.setText(transaction.getCategory());
        amount_lbl.setText("‚Çπ" + String.format("%.2f", transaction.getAmount()));
        sender_lbl.setText(transaction.getSender());
        receiver_lbl.setText(transaction.getReceiver());
        trans_date_lbl.setText(transaction.getDate().format(DateTimeFormatter.ofPattern("yyyy-MM-dd")));

        // Set icon based on direction
        Client currentClient = Model.getInstance().getLoggedInClient();
        if (currentClient != null) {
            String currentAddress = currentClient.getPayeeAddress();

            if (transaction.getReceiver().equals(currentAddress)) {
                // Incoming transaction
                in_icon.setImage(new Image(getClass().getResourceAsStream("/icons/incoming.png")));
            } else {
                // Outgoing transaction
                in_icon.setImage(new Image(getClass().getResourceAsStream("/icons/outgoing.png")));
            }
        }
    }
}

