package com.example.wealthwise.Controller.Client;

import com.example.wealthwise.Models.Client;
import com.example.wealthwise.Models.Model;
import com.example.wealthwise.Models.Transaction;
import javafx.fxml.Initializable;
import javafx.scene.control.ListView;

import java.net.URL;
import java.util.ResourceBundle;

public class TransactionController implements Initializable {

    public ListView<String> transactions_listview;

    @Override
    public void initialize(URL url, ResourceBundle resourceBundle) {
        Client client = Model.getInstance().getLoggedInClient();
        if (client != null) {
            // Load transactions from client
            client.getTransactionHistory().forEach(tx -> {
                transactions_listview.getItems().add(formatTransaction(tx));
            });

            // Optional: refresh automatically when Model.refreshClientData() is called
            client.getTransactionHistory().addListener((javafx.collections.ListChangeListener<Transaction>) change -> {
                transactions_listview.getItems().clear();
                client.getTransactionHistory().forEach(tx -> transactions_listview.getItems().add(formatTransaction(tx)));
            });
        }
    }

    private String formatTransaction(Transaction tx) {
        return String.format("%s ‚Üí %s | ‚Çπ%.2f | %s | %s",
                tx.senderProperty().get(),
                tx.receiverProperty().get(),
                tx.amountProperty().get(),
                tx.categoryProperty().get(),
                tx.dateProperty().get());
    }
}

