package com.example.wealthwise.Controller.Client;

import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import javafx.fxml.Initializable;
import javafx.scene.control.Button;
import javafx.scene.control.ListView;
import java.net.URL;
import java.util.ResourceBundle;

public class BudgetController implements Initializable {
    public Button create_budget_btn;
    public ListView<String> budget_list;

    private ObservableList<String> budgets = FXCollections.observableArrayList();

    @Override
    public void initialize(URL url, ResourceBundle resourceBundle) {
        budget_list.setItems(budgets);

        // Add some sample budgets for demonstration
        budgets.add("Monthly Budget: $2000");
        budgets.add("Food Budget: $400");
        budgets.add("Entertainment Budget: $200");

        // Handle create budget button click
        create_budget_btn.setOnAction(event -> {
            // For now, just add a sample budget
            budgets.add("New Budget: $1000");
        });
    }
}

