package com.example.wealthwise.Models;

import com.example.wealthwise.Views.AccountType;
import com.example.wealthwise.Views.ViewFactory;

import java.sql.ResultSet;
import java.time.LocalDate;

public class Model {
    private final ViewFactory viewFactory;
    private static Model model;
    private final DatabaseDriver databaseDriver;
    private AccountType loginaccountType= AccountType.CLIENT;

    public AccountType getLoginaccountType() {return loginaccountType;}
    public void setLoginaccountType(AccountType loginaccountType){this.loginaccountType=loginaccountType;}
    //Cient Data Section
   private final Client client;
   private Boolean clientLoginSuccessFlag;

    //Admin Data Section
    private final Admin admin;
    private Boolean adminLoginSuccessFlag;


    private Model(){
        this.viewFactory = new ViewFactory();
        this.databaseDriver= new DatabaseDriver();

        //client Section
        this.clientLoginSuccessFlag=false;
        this.client=new Client("","","",null,null,null);
        //Admin Section
        this.adminLoginSuccessFlag = false;
        this.admin = new Admin("");

    }
    public static synchronized Model getInstance() {
        if(model==null){
            model=new Model();

        }
        return model;
    }
    public ViewFactory getViewFactory() {
        return viewFactory;
    }

    public DatabaseDriver getDatabaseDriver(){return new DatabaseDriver();}

    // client Methods
    public Boolean getClientLoginSuccessFlag() {return clientLoginSuccessFlag;}
    public void setClientLoginSuccessFlag(Boolean Flag) {this.clientLoginSuccessFlag = Flag;}
    public Client getClient() {return client;}

    public void evaluateclientCred(String pAddress, String password){
        ResultSet resultSet=this.databaseDriver.getClientData(pAddress,password);
        try{
           if(resultSet.isBeforeFirst()){
               this.client.firstNameProperty().set(resultSet.getString("FirstName"));
               this.client.lastNameProperty().set(resultSet.getString("LastName"));
               this.client.payeeAddressProperty().set(resultSet.getString("PayeeAddress"));
               String[] dateParts= resultSet.getString("Date").split("-");
               LocalDate date = LocalDate.of(Integer.parseInt(dateParts[0]),Integer.parseInt(dateParts[1]),Integer.parseInt(dateParts[2]));
               this.client.dateProperty().set(date);
               this.clientLoginSuccessFlag=true;


           }
        }
        catch(Exception e){
            e.printStackTrace();
        }
    }

    // Admin Methods
    public Boolean getAdminLoginSuccessFlag() { return adminLoginSuccessFlag; }
    public void setAdminLoginSuccessFlag(Boolean flag) { this.adminLoginSuccessFlag = flag; }
    public Admin getAdmin() { return admin; }

    public void evaluateAdminCred(String username, String password) {
        ResultSet resultSet = this.databaseDriver.getAdminData(username, password);
        try {
            if (resultSet != null && resultSet.next()) {
                String adminUsername = resultSet.getString("Username");
                this.admin.usernameProperty().set(adminUsername);
                this.adminLoginSuccessFlag = true;
            } else {
                this.adminLoginSuccessFlag = false;
            }
        } catch (Exception e) {
            e.printStackTrace();
            this.adminLoginSuccessFlag = false;
        }
    }

}

