package com.example.wealthwise.Controller.Admin;

import com.example.wealthwise.Models.Model;
import javafx.fxml.FXML;
import javafx.fxml.Initializable;
import javafx.scene.control.*;
import javafx.scene.control.Alert.AlertType;
import javafx.util.Duration;
import javafx.animation.PauseTransition;

import java.net.URL;
import java.util.ResourceBundle;

public class CreateClientController implements Initializable {
    @FXML
    public TextField first_name;
    @FXML
    public TextField last_name;
    @FXML
    public TextField password;
    @FXML
    public TextField payee_address; // Added payee address field
    @FXML
    public CheckBox payee_address_wallet;
    @FXML
    public CheckBox add_wallet_check;
    @FXML
    public TextField wallet_balance;
    @FXML
    public CheckBox saving_check;
    @FXML
    public TextField account_balance;
    @FXML
    public Button create_client_button; // Fixed to match FXML
    @FXML
    public Label error_lbl;

    private Model model;

    @Override
    public void initialize(URL location, ResourceBundle resources) {
        model = Model.getInstance();

        // Set up the create client button action
        create_client_button.setOnAction(event -> onCreateClient());

        // Hide error label initially
        if (error_lbl != null) {
            error_lbl.setVisible(false);
        }
    }

    @FXML
    private void onCreateClient() {
        // Clear any previous error messages
        if (error_lbl != null) {
            error_lbl.setVisible(false);
        }

        // Get input values
        String firstName = first_name.getText().trim();
        String lastName = last_name.getText().trim();
        String userPassword = password.getText().trim();
        String payeeAddress = payee_address != null ? payee_address.getText().trim() : "";

        // Validate inputs
        if (firstName.isEmpty() || lastName.isEmpty() || userPassword.isEmpty() || payeeAddress.isEmpty()) {
            showErrorMessage("Please fill in all required fields.");
            return;
        }

        // Check if payee address is available
        if (!model.isPayeeAddressAvailable(payeeAddress)) {
            showErrorMessage("Payee address already exists. Please use a different email.");
            return;
        }

        // Create the client with detailed error reporting
        Model.ClientCreationResult result = model.createNewClientWithResult(firstName, lastName, payeeAddress, userPassword, false);

        if (result.isSuccess()) {
            // Show success popup
            showSuccessNotification("Client Created Successfully!", 
                "New client " + firstName + " " + lastName + " has been created successfully!");

            // Clear the form
            clearForm();
        } else {
            // Show detailed error message
            showDetailedErrorMessage("Client Creation Failed", result.getErrorMessage());
        }
    }

    private void showSuccessNotification(String title, String message) {
        Alert alert = new Alert(AlertType.INFORMATION);
        alert.setTitle(title);
        alert.setHeaderText(null);
        alert.setContentText(message);

        // Show the alert and auto-close after 3 seconds
        alert.show();

        // Auto-close the alert after 3 seconds
        PauseTransition delay = new PauseTransition(Duration.seconds(3));
        delay.setOnFinished(event -> alert.close());
        delay.play();
    }

    private void showErrorMessage(String message) {
        if (error_lbl != null) {
            error_lbl.setText(message);
            error_lbl.setVisible(true);
        } else {
            // Fallback to alert if error label is not available
            Alert alert = new Alert(AlertType.ERROR);
            alert.setTitle("Error");
            alert.setHeaderText(null);
            alert.setContentText(message);
            alert.showAndWait();
        }
    }

    private void showDetailedErrorMessage(String title, String detailedMessage) {
        // Show detailed error in popup (always use popup for detailed errors)
        Alert alert = new Alert(AlertType.ERROR);
        alert.setTitle(title);
        alert.setHeaderText("Client Creation Failed");
        alert.setContentText(detailedMessage);

        // Make the dialog resizable for long error messages
        alert.setResizable(true);
        alert.getDialogPane().setPrefSize(480, 320);

        // Show the alert and auto-close after 6 seconds
        alert.show();

        // Auto-close the alert after 6 seconds
        PauseTransition delay = new PauseTransition(Duration.seconds(6));
        delay.setOnFinished(event -> alert.close());
        delay.play();

        // Also show a brief message in the error label if available
        if (error_lbl != null) {
            error_lbl.setText("Client creation failed - see popup for details");
            error_lbl.setVisible(true);
        }
    }

    private void clearForm() {
        first_name.clear();
        last_name.clear();
        password.clear();
        if (payee_address != null) {
            payee_address.clear();
        }
        if (wallet_balance != null) {
            wallet_balance.clear();
        }
        if (account_balance != null) {
            account_balance.clear();
        }

        // Reset checkboxes
        if (payee_address_wallet != null) {
            payee_address_wallet.setSelected(false);
        }
        if (add_wallet_check != null) {
            add_wallet_check.setSelected(false);
        }
        if (saving_check != null) {
            saving_check.setSelected(false);
        }
    }
}

