package com.smartfinance.Models;

import java.sql.*;
import java.time.LocalDate;

/**
 * DatabaseDriver - Handles all DB operations for Smart Finance app.
 * Includes auto table creation, client/account management, and transaction/investment support.
 */
public class DatabaseDriver {
    private Connection connection;

    public DatabaseDriver() {
        try {
            this.connection = DriverManager.getConnection("jdbc:sqlite:mazebank.db");
            System.out.println("[DatabaseDriver] ‚úÖ Connected to database successfully.");
            try (Statement stmt = connection.createStatement()) {
                stmt.execute("PRAGMA foreign_keys = ON");
            }
            ensureTablesExist();
            ensureDefaultAdmin();
        } catch (SQLException e) {
            System.err.println("[DatabaseDriver] ‚ùå Connection failed!");
            e.printStackTrace();
        }
    }

    public Connection getConnection() {
        return connection;
    }

    // ====================================================
    // TABLE CREATION
    // ====================================================
    private void ensureTablesExist() {
        try (Statement stmt = connection.createStatement()) {

            stmt.execute("""
                CREATE TABLE IF NOT EXISTS Clients (
                    ID INTEGER PRIMARY KEY AUTOINCREMENT,
                    FirstName TEXT,
                    LastName TEXT,
                    PayeeAddress TEXT UNIQUE,
                    Password TEXT,
                    Date TEXT
                )
            """);

            stmt.execute("""
                CREATE TABLE IF NOT EXISTS Admins (
                    ID INTEGER PRIMARY KEY AUTOINCREMENT,
                    Username TEXT UNIQUE,
                    Password TEXT
                )
            """);

            stmt.execute("""
                CREATE TABLE IF NOT EXISTS WalletAccounts (
                    ID INTEGER PRIMARY KEY AUTOINCREMENT,
                    Owner TEXT,
                    AccNumber TEXT,
                    Balance REAL,
                    TransactionLimit REAL,
                    date_created TEXT,
                    FOREIGN KEY (Owner) REFERENCES Clients(PayeeAddress) ON DELETE CASCADE
                )
            """);

            stmt.execute("""
                CREATE TABLE IF NOT EXISTS SavingsAccounts (
                    ID INTEGER PRIMARY KEY AUTOINCREMENT,
                    Owner TEXT,
                    AccNumber TEXT,
                    Balance REAL,
                    TransactionLimit REAL,
                    date_created TEXT,
                    FOREIGN KEY (Owner) REFERENCES Clients(PayeeAddress) ON DELETE CASCADE
                )
            """);

            stmt.execute("""
                CREATE TABLE IF NOT EXISTS Transactions (
                    ID INTEGER PRIMARY KEY AUTOINCREMENT,
                    Sender TEXT,
                    Receiver TEXT,
                    Amount REAL,
                    Category TEXT,
                    Message TEXT,
                    Date TEXT
                )
            """);

            stmt.execute("""
                CREATE TABLE IF NOT EXISTS Investments (
                    ID INTEGER PRIMARY KEY AUTOINCREMENT,
                    Owner TEXT,
                    InvestmentType TEXT,
                    AmountInvested REAL,
                    CurrentValue REAL,
                    DateInvested TEXT,
                    FOREIGN KEY (Owner) REFERENCES Clients(PayeeAddress) ON DELETE CASCADE
                )
            """);

        } catch (SQLException e) {
            e.printStackTrace();
        }
    }

    // ====================================================
    // DEFAULT ADMIN CREATION
    // ====================================================
    private void ensureDefaultAdmin() {
        try (PreparedStatement stmt = connection.prepareStatement(
                "INSERT OR IGNORE INTO Admins (Username, Password) VALUES (?, ?)"
        )) {
            stmt.setString(1, "admin");
            stmt.setString(2, "admin123");
            stmt.executeUpdate();
            System.out.println("[DatabaseDriver] üß© Default admin ensured (username: admin)");
        } catch (SQLException e) {
            e.printStackTrace();
        }
    }

    // ====================================================
    // AUTHENTICATION
    // ====================================================
    public ResultSet getClientsData(String payeeAddress, String password) {
        return executeQuery(
                "SELECT * FROM Clients WHERE PayeeAddress = ? AND Password = ?",
                payeeAddress, password
        );
    }

    public ResultSet getAdminData(String username, String password) {
        return executeQuery(
                "SELECT * FROM Admins WHERE Username = ? AND Password = ?",
                username, password
        );
    }

    // ====================================================
    // ACCOUNT FETCH
    // ====================================================
    public ResultSet getWalletAccount(String owner) {
        return executeQuery("SELECT * FROM WalletAccounts WHERE Owner = ?", owner);
    }

    public ResultSet getSavingsAccount(String owner) {
        return executeQuery("SELECT * FROM SavingsAccounts WHERE Owner = ?", owner);
    }

    // ====================================================
    // ACCOUNT CREATION
    // ====================================================
    public String getNextAccountNumber(String table) {
        try (Statement stmt = connection.createStatement();
             ResultSet rs = stmt.executeQuery("SELECT MAX(ID) FROM " + table)) {
            if (rs.next()) {
                int next = rs.getInt(1) + 1;
                return String.format("%04d", next);
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
        return "0001";
    }

    public void createWalletAccount(String owner, double balance) {
        String accNumber = getNextAccountNumber("WalletAccounts");
        LocalDate date = LocalDate.now();
        executeUpdate(
                "INSERT INTO WalletAccounts (Owner, AccNumber, Balance, TransactionLimit, date_created) VALUES (?, ?, ?, ?, ?)",
                owner, accNumber, balance, 10000, date.toString()
        );
    }

    public void createSavingsAccount(String owner, double balance) {
        String accNumber = getNextAccountNumber("SavingsAccounts");
        LocalDate date = LocalDate.now();
        executeUpdate(
                "INSERT INTO SavingsAccounts (Owner, AccNumber, Balance, TransactionLimit, date_created) VALUES (?, ?, ?, ?, ?)",
                owner, accNumber, balance, 100000, date.toString()
        );
    }

    // ====================================================
    // CLIENT MANAGEMENT
    // ====================================================
    public boolean createClient(String firstName, String lastName, String payeeAddress, String password,
                                double savingsBalance, double walletBalance) {
        LocalDate date = LocalDate.now();
        try {
            connection.setAutoCommit(false);

            executeUpdate(
                    "INSERT INTO Clients (FirstName, LastName, PayeeAddress, Password, Date) VALUES (?, ?, ?, ?, ?)",
                    firstName, lastName, payeeAddress, password, date.toString()
            );

            createWalletAccount(payeeAddress, walletBalance);
            createSavingsAccount(payeeAddress, savingsBalance);

            connection.commit();
            return true;
        } catch (SQLException e) {
            try { connection.rollback(); } catch (SQLException ignored) {}
            e.printStackTrace();
            return false;
        } finally {
            try { connection.setAutoCommit(true); } catch (SQLException ignored) {}
        }
    }

    public ResultSet getAllClientsData() {
        return executeQuery("SELECT * FROM Clients");
    }

    public ResultSet searchClients(String searchText) {
        String query = "%" + searchText + "%";
        return executeQuery(
                "SELECT * FROM Clients WHERE PayeeAddress LIKE ? OR FirstName LIKE ? OR LastName LIKE ?",
                query, query, query
        );
    }

    public boolean deleteClient(String payeeAddress) {
        try {
            connection.setAutoCommit(false);

            executeUpdate("DELETE FROM WalletAccounts WHERE Owner = ?", payeeAddress);
            executeUpdate("DELETE FROM SavingsAccounts WHERE Owner = ?", payeeAddress);
            int deleted = executeUpdate("DELETE FROM Clients WHERE PayeeAddress = ?", payeeAddress);

            connection.commit();
            return deleted > 0;
        } catch (SQLException e) {
            try { connection.rollback(); } catch (SQLException ignored) {}
            e.printStackTrace();
            return false;
        } finally {
            try { connection.setAutoCommit(true); } catch (SQLException ignored) {}
        }
    }

    // ====================================================
    // TRANSACTIONS
    // ====================================================
    public void createTransaction(String sender, String receiver, double amount, String category, String message) {
        executeUpdate(
                "INSERT INTO Transactions (Sender, Receiver, Amount, Category, Message, Date) VALUES (?, ?, ?, ?, ?, datetime('now'))",
                sender, receiver, amount, category, message
        );
    }

    public ResultSet getTransactionsByPayee(String payeeAddress) {
        return executeQuery(
                "SELECT * FROM Transactions WHERE Sender = ? OR Receiver = ? ORDER BY Date DESC",
                payeeAddress, payeeAddress
        );
    }

    public ResultSet getExpendituresByCategory(String payeeAddress) {
        return executeQuery(
                "SELECT Category, SUM(Amount) as Total FROM Transactions WHERE Sender = ? GROUP BY Category",
                payeeAddress
        );
    }

    // ====================================================
    // INVESTMENTS
    // ====================================================
    public ResultSet getInvestments(String owner) {
        return executeQuery("SELECT * FROM Investments WHERE Owner = ? ORDER BY DateInvested DESC", owner);
    }

    public void createInvestment(String owner, String investmentType, double amountInvested, double currentValue) {
        executeUpdate(
                "INSERT INTO Investments (Owner, InvestmentType, AmountInvested, CurrentValue, DateInvested) VALUES (?, ?, ?, ?, datetime('now'))",
                owner, investmentType, amountInvested, currentValue
        );
    }

    public boolean updateInvestmentValue(int id, double newValue) {
        return executeUpdate("UPDATE Investments SET CurrentValue = ? WHERE ID = ?", newValue, id) > 0;
    }

    // ====================================================
    // BALANCE OPERATIONS
    // ====================================================
    public boolean depositToSavings(String owner, double amount) {
        return executeUpdate("UPDATE SavingsAccounts SET Balance = Balance + ? WHERE Owner = ?", amount, owner) > 0;
    }

    public boolean depositToWallet(String owner, double amount) {
        return executeUpdate("UPDATE WalletAccounts SET Balance = Balance + ? WHERE Owner = ?", amount, owner) > 0;
    }

    public void updateSavingsBalance(String owner, double newBalance) {
        executeUpdate("UPDATE SavingsAccounts SET Balance = ? WHERE Owner = ?", newBalance, owner);
    }

    public void updateWalletBalance(String owner, double newBalance) {
        executeUpdate("UPDATE WalletAccounts SET Balance = ? WHERE Owner = ?", newBalance, owner);
    }

    public boolean updateBalance(String table, String owner, double newBalance) {
        String sql = "UPDATE " + table + " SET Balance = ? WHERE Owner = ?";
        return executeUpdate(sql, newBalance, owner) > 0;
    }

    // ====================================================
    // HELPER METHODS
    // ====================================================
    private ResultSet executeQuery(String sql, Object... params) {
        try {
            PreparedStatement stmt = connection.prepareStatement(sql);
            for (int i = 0; i < params.length; i++)
                stmt.setObject(i + 1, params[i]);
            return stmt.executeQuery();
        } catch (SQLException e) {
            e.printStackTrace();
            return null;
        }
    }

    private int executeUpdate(String sql, Object... params) {
        try (PreparedStatement stmt = connection.prepareStatement(sql)) {
            for (int i = 0; i < params.length; i++)
                stmt.setObject(i + 1, params[i]);
            return stmt.executeUpdate();
        } catch (SQLException e) {
            e.printStackTrace();
            return 0;
        }
    }


}

package com.smartfinance.Controller.Admin;

import com.smartfinance.Models.Model;
import javafx.fxml.FXML;
import javafx.fxml.Initializable;
import javafx.scene.control.Button;
import javafx.scene.control.Label;
import javafx.scene.control.TextField;

import java.net.URL;
import java.util.ResourceBundle;

public class CreateClientController implements Initializable {

    @FXML
    private TextField first_name;
    @FXML
    private TextField last_name;
    @FXML
    private TextField password;
    @FXML
    private TextField payee_address;
    @FXML
    private TextField Wallet_Balance;
    @FXML
    private TextField Savings_Balance;
    @FXML
    private Button create_client_button;
    @FXML
    private Label error_lbl;

    @Override
    public void initialize(URL url, ResourceBundle resourceBundle) {
        create_client_button.setOnAction(event -> onCreateClient());
    }

    private void onCreateClient() {
        String firstName = first_name.getText().trim();
        String lastName = last_name.getText().trim();
        String pAddress = payee_address.getText().trim();
        String pass = password.getText().trim();

        // ‚úÖ FIX: Remove invalid double assignment (cannot assign TextField to double)
        double walletBalance;
        double savingsBalance;

        try {
            walletBalance = Double.parseDouble(Wallet_Balance.getText().trim());
            savingsBalance = Double.parseDouble(Savings_Balance.getText().trim());
        } catch (NumberFormatException e) {
            error_lbl.setText("‚ö† Please enter valid numbers for Wallet and Savings balance");
            return;
        }

        // ‚úÖ Validation for empty fields
        if (firstName.isEmpty() || lastName.isEmpty() || pAddress.isEmpty() || pass.isEmpty()) {
            error_lbl.setText("‚ö† Please fill all the fields");
            return;
        }

        // ‚úÖ Create client (assuming Model handles DB + account creation)
        boolean success = Model.getInstance().createNewClient(firstName, lastName, pAddress, pass, walletBalance, savingsBalance);

        if (success) {
            error_lbl.setText("‚úÖ Client created successfully!");
            clearFields();
        } else {
            error_lbl.setText("‚ùå Failed to create client. Try again.");
        }
    }

    private void clearFields() {
        first_name.clear();
        last_name.clear();
        payee_address.clear();
        password.clear();
        Wallet_Balance.clear();
        Savings_Balance.clear();
    }
}

package com.smartfinance.Models;

import com.smartfinance.Views.ViewFactory;
import javafx.collections.FXCollections;
import javafx.collections.ObservableList;

import java.sql.ResultSet;
import java.sql.SQLException;
import java.time.LocalDate;
import java.util.ArrayList;
import java.util.List;
import java.util.function.Consumer;

// Forcing a file update
public class Model {
    private final ViewFactory viewFactory;
    private final DatabaseDriver databaseDriver;
    private static Model model;

    private boolean clientLoginSuccessFlag;
    private boolean adminLoginSuccessFlag;
    private Client loggedInClient;

    private Consumer<Client> clientCreatedListener;
    private Consumer<Client> clientDeletedListener;
    private Runnable onClientDataRefreshed;

    private Model() {
        this.viewFactory = new ViewFactory();
        this.databaseDriver = new DatabaseDriver();
    }

    public static synchronized Model getInstance() {
        if (model == null) model = new Model();
        return model;
    }

    public ViewFactory getViewFactory() { return viewFactory; }
    public DatabaseDriver getDatabaseDriver() { return databaseDriver; }

    // =====================================================
    // ACCOUNT LOADERS (auto-fetched from DB)
    // =====================================================
    public WalletAccount getWalletAccount(String owner) {
        try (ResultSet rs = databaseDriver.getWalletAccount(owner)) {
            if (rs != null && rs.next()) {
                return new WalletAccount(
                        owner,
                        rs.getString("AccNumber"),
                        rs.getDouble("Balance"),
                        (int) rs.getDouble("TransactionLimit"),
                        rs.getString("date_created")
                );
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
        return null;
    }

    public SavingsAccount getSavingsAccount(String owner) {
        try (ResultSet rs = databaseDriver.getSavingsAccount(owner)) {
            if (rs != null && rs.next()) {
                return new SavingsAccount(
                        owner,
                        rs.getString("AccNumber"),
                        rs.getDouble("Balance"),
                        rs.getDouble("TransactionLimit"),
                        rs.getString("date_created")
                );
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
        return null;
    }

    // =====================================================
    // CLIENT MANAGEMENT
    // =====================================================
    public boolean createNewClient(String firstName, String lastName, String payeeAddress, String password,
                                   double walletBalance, double savingsBalance) {
        LocalDate date = LocalDate.now();

        boolean created = databaseDriver.createClient(firstName, lastName, payeeAddress, password, walletBalance, savingsBalance);

        if (!created) return false;

        if (clientCreatedListener != null) {
            WalletAccount wallet = getWalletAccount(payeeAddress);
            SavingsAccount savings = getSavingsAccount(payeeAddress);
            Client newClient = new Client(firstName, lastName, payeeAddress, wallet, savings, date);
            newClient.setTransactionHistory(loadClientTransactions(payeeAddress));
            clientCreatedListener.accept(newClient);
        }
        return true;
    }


    public boolean deleteClient(Client client) {
        try {
            boolean deleted = databaseDriver.deleteClient(client.getPayeeAddress());
            if (deleted && clientDeletedListener != null) {
                clientDeletedListener.accept(client);
            }
            return deleted;
        } catch (Exception e) {
            e.printStackTrace();
            return false;
        }
    }

    // =====================================================
    // LOGIN HANDLERS
    // =====================================================
    public void evaluateClientCred(String payeeAddress, String password) {
        try (ResultSet rs = databaseDriver.getClientsData(payeeAddress, password)) {
            clientLoginSuccessFlag = rs != null && rs.next();
            if (clientLoginSuccessFlag) {
                String fName = rs.getString("FirstName");
                String lName = rs.getString("LastName");
                String pAddress = rs.getString("PayeeAddress");
                LocalDate date = LocalDate.parse(rs.getString("Date"));

                WalletAccount wallet = getWalletAccount(pAddress);
                SavingsAccount savings = getSavingsAccount(pAddress);

                loggedInClient = new Client(fName, lName, pAddress, wallet, savings, date);
                loggedInClient.setTransactionHistory(loadClientTransactions(pAddress));
            }
        } catch (SQLException e) {
            e.printStackTrace();
            clientLoginSuccessFlag = false;
        }
    }

    public void evaluateAdminCred(String username, String password) {
        try (ResultSet rs = databaseDriver.getAdminData(username, password)) {
            adminLoginSuccessFlag = rs != null && rs.next();
        } catch (SQLException e) {
            e.printStackTrace();
            adminLoginSuccessFlag = false;
        }
    }

    // =====================================================
    // FLAGS & LISTENERS
    // =====================================================
    public boolean getClientLoginSuccessFlag() { return clientLoginSuccessFlag; }
    public boolean getAdminLoginSuccessFlag() { return adminLoginSuccessFlag; }
    public Client getLoggedInClient() { return loggedInClient; }

    public void setClientCreatedListener(Consumer<Client> listener) { this.clientCreatedListener = listener; }
    public void setClientDeletedListener(Consumer<Client> listener) { this.clientDeletedListener = listener; }
    public void setOnClientDataRefreshed(Runnable listener) { this.onClientDataRefreshed = listener; }

    // =====================================================
    // CLIENT UTILITIES
    // =====================================================
    public List<Client> getAllClients() {
        List<Client> clients = new ArrayList<>();
        try (ResultSet rs = databaseDriver.getAllClientsData()) {
            while (rs != null && rs.next()) {
                String fName = rs.getString("FirstName");
                String lName = rs.getString("LastName");
                String pAddress = rs.getString("PayeeAddress");
                LocalDate date = LocalDate.parse(rs.getString("Date"));

                WalletAccount wallet = getWalletAccount(pAddress);
                SavingsAccount savings = getSavingsAccount(pAddress);

                Client client = new Client(fName, lName, pAddress, wallet, savings, date);
                client.setTransactionHistory(loadClientTransactions(pAddress));
                clients.add(client);
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
        return clients;
    }

    public boolean clientExists(String payeeAddress) {
        try (ResultSet rs = databaseDriver.searchClients(payeeAddress)) {
            return rs != null && rs.next();
        } catch (SQLException e) {
            e.printStackTrace();
            return false;
        }
    }

    // =====================================================
    // TRANSACTIONS
    // =====================================================
    public boolean transferMoney(String senderAddress, String receiverAddress, double amount, String category, String message) {
        DatabaseDriver db = getDatabaseDriver();
        try {
            db.getConnection().setAutoCommit(false);

            try (ResultSet senderSavings = db.getSavingsAccount(senderAddress)) {
                if (senderSavings == null || !senderSavings.next()) return false;

                double senderBalance = senderSavings.getDouble("Balance");
                if (senderBalance < amount) return false;

                db.updateBalance("SavingsAccounts", senderAddress, senderBalance - amount);
            }

            try (ResultSet receiverWallet = db.getWalletAccount(receiverAddress)) {
                if (receiverWallet != null && receiverWallet.next()) {
                    double receiverBalance = receiverWallet.getDouble("Balance");
                    db.updateBalance("WalletAccounts", receiverAddress, receiverBalance + amount);
                } else {
                    // Create wallet account if it doesn't exist
                    db.createWalletAccount(receiverAddress, amount);
                }
            }

            db.createTransaction(senderAddress, receiverAddress, amount, category, message);

            db.getConnection().commit();
            return true;
        } catch (SQLException e) {
            try { db.getConnection().rollback(); } catch (SQLException ignored) {}
            e.printStackTrace();
            return false;
        } finally {
            try { db.getConnection().setAutoCommit(true); } catch (SQLException ignored) {}
        }
    }

    // =====================================================
    // LOAD TRANSACTIONS
    // =====================================================
    public ObservableList<Transaction> loadClientTransactions(String payeeAddress) {
        ObservableList<Transaction> list = FXCollections.observableArrayList();
        try (ResultSet rs = databaseDriver.getTransactionsByPayee(payeeAddress)) {
            while (rs != null && rs.next()) {
                String category = rs.getString("Category");
                if (category == null || category.trim().isEmpty()) category = "Transfer";

                String dateStr = rs.getString("Date");
                LocalDate date = (dateStr != null) ?
                        LocalDate.parse(dateStr.split(" ")[0]) : LocalDate.now();

                list.add(new Transaction(
                        rs.getString("Sender"),
                        rs.getString("Receiver"),
                        rs.getDouble("Amount"),
                        category,
                        date,
                        rs.getString("Message")
                ));
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
        return list;
    }

    // =====================================================
    // DASHBOARD REFRESH
    // =====================================================
    public void refreshClientData() {
        if (loggedInClient != null) {
            String payee = loggedInClient.getPayeeAddress();
            loggedInClient.walletAccountProperty().set(getWalletAccount(payee));
            loggedInClient.savingsAccountProperty().set(getSavingsAccount(payee));
            loggedInClient.setTransactionHistory(loadClientTransactions(payee));

            if (onClientDataRefreshed != null) onClientDataRefreshed.run();
        }
    }
}

