package com.smartfinance.service;

import com.smartfinance.Models.Model;
import com.smartfinance.Models.RiskProfile;
import org.json.JSONArray;
import org.json.JSONObject;

import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

public class InvestmentService {
    private final APIClient apiClient;

    public InvestmentService() {
        this.apiClient = new APIClient();
    }

    public List<String> getStockSuggestions() {
        List<String> suggestions = new ArrayList<>();
        try {
            // Get risk profile from logged-in client
            Model model = Model.getInstance();
            RiskProfile risk = model.getLoggedInClient().getRiskProfile();

            String endpoint;
            if (risk == RiskProfile.CONSERVATIVE) {
                // For conservative, use stable stocks or bonds, but since API is stocks, use NSE most active but filter low volatility
                endpoint = "/NSE_most_active"; // Placeholder
            } else if (risk == RiskProfile.MODERATE) {
                endpoint = "/trending";
            } else { // AGGRESSIVE
                endpoint = "/NSE_most_active";
            }

            String response = apiClient.getTrendingStocks(); // Use trending for simplicity
            System.out.println("API Response: " + response);
            JSONObject json = new JSONObject(response);

            // Handle the new nested response format
            JSONArray data = new JSONArray();
            if (json.has("trending_stocks")) {
                JSONObject trendingStocks = json.getJSONObject("trending_stocks");

                // Add top gainers
                if (trendingStocks.has("top_gainers")) {
                    JSONArray gainers = trendingStocks.getJSONArray("top_gainers");
                    for (int i = 0; i < gainers.length(); i++) {
                        data.put(gainers.getJSONObject(i));
                    }
                }

                // Add top losers (for diversity, but prioritize gainers)
                if (trendingStocks.has("top_losers") && data.length() < 5) {
                    JSONArray losers = trendingStocks.getJSONArray("top_losers");
                    for (int i = 0; i < Math.min(5 - data.length(), losers.length()); i++) {
                        data.put(losers.getJSONObject(i));
                    }
                }
            } else {
                // Fallback to old format
                if (json.has("data") && !json.isNull("data")) {
                    try {
                        data = json.getJSONArray("data");
                    } catch (Exception e) {
                        // Maybe it's an object instead of array
                        JSONObject singleData = json.getJSONObject("data");
                        data = new JSONArray();
                        data.put(singleData);
                    }
                } else if (json.has("stocks") && !json.isNull("stocks")) {
                    data = json.getJSONArray("stocks");
                }
            }

            for (int i = 0; i < Math.min(5, data.length()); i++) {
                JSONObject stock = data.getJSONObject(i);
                // Use ticker_id instead of symbol for the new API format
                String symbol = stock.has("ticker_id") ? stock.getString("ticker_id") :
                               (stock.has("symbol") ? stock.getString("symbol") : "");
                if (!symbol.isEmpty()) {
                    suggestions.add(symbol);
                }
            }

        } catch (IOException | InterruptedException e) {
            e.printStackTrace();
        }
        return suggestions;
    }

    public List<Double> getHistoricalPrices(String symbol, String period) {
        List<Double> prices = new ArrayList<>();
        try {
            String response = apiClient.getHistoricalData(symbol, period);
            System.out.println("Historical Data Response: " + response);
            JSONObject json = new JSONObject(response);

            JSONArray data;
            if (json.has("data")) {
                data = json.getJSONArray("data");
            } else if (json.has("prices")) {
                data = json.getJSONArray("prices");
            } else {
                data = new JSONArray();
            }

            for (int i = 0; i < data.length(); i++) {
                JSONObject point = data.getJSONObject(i);
                if (point.has("close")) {
                    prices.add(point.getDouble("close"));
                } else if (point.has("price")) {
                    prices.add(point.getDouble("price"));
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        return prices;
    }
}

