package com.smartfinance.Controller.Client;

import com.smartfinance.Models.*;
import com.smartfinance.service.InvestmentService;
import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import javafx.fxml.FXML;
import javafx.fxml.Initializable;
import javafx.scene.chart.LineChart;
import javafx.scene.chart.NumberAxis;
import javafx.scene.chart.XYChart;
import javafx.scene.control.Button;
import javafx.scene.control.ChoiceBox;
import javafx.scene.control.ListView;
import javafx.scene.control.TextField;
import javafx.scene.layout.HBox;
import javafx.scene.layout.VBox;
import javafx.scene.text.Text;

import java.net.URL;
import java.sql.ResultSet;
import java.sql.SQLException;
import java.util.Arrays;
import java.util.List;
import java.util.ResourceBundle;

public class InvestmentController implements Initializable {

    @FXML
    private ChoiceBox<String> investment_type_choicebox;
    @FXML
    private TextField investment_amount_field;
    @FXML
    private Button add_investment_btn;
    @FXML
    private ListView<String> investments_listview;
    @FXML
    private VBox recommendations_container;

    private ObservableList<String> investmentsList = FXCollections.observableArrayList();
    private InvestmentService investmentService;

    @Override
    public void initialize(URL location, ResourceBundle resources) {
        investmentService = new InvestmentService();

        // Populate investment types
        investment_type_choicebox.getItems().addAll(RiskProfile.values()); // For now just stocks
        investment_type_choicebox.setValue("STOCKS"); // Default

        // Set up list views
        investments_listview.setItems(investmentsList);

        // Load existing investments and recommendations
        loadInvestments();
        loadRecommendations();
    }

    private void loadInvestments() {
        investmentsList.clear();
        Model model = Model.getInstance();
        String owner = model.getLoggedInClient().getPayeeAddress();

        try (ResultSet rs = model.getDatabaseDriver().getInvestments(owner)) {
            while (rs != null && rs.next()) {
                Investment investment = new Investment(
                        rs.getInt("ID"),
                        rs.getString("Owner"),
                        rs.getString("InvestmentType"),
                        rs.getDouble("AmountInvested"),
                        rs.getDouble("CurrentValue"),
                        rs.getString("DateInvested")
                );
                String display = String.format("%s - Invested: $%.2f, Current: $%.2f, P/L: $%.2f (%.2f%%)",
                        investment.getInvestmentType(),
                        investment.getAmountInvested(),
                        investment.getCurrentValue(),
                        investment.getProfitLoss(),
                        investment.getProfitLossPercentage());
                investmentsList.add(display);
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
    }

    private void loadRecommendations() {
        recommendations_container.getChildren().clear();
        List<String> suggestions = investmentService.getStockSuggestions();
        for (String symbol : suggestions) {
            recommendations_container.getChildren().add(createStockContainer(symbol));
        }
    }

    private VBox createStockContainer(String symbol) {
        VBox container = new VBox(8);
        container.setStyle("-fx-background-color: #f8f9fa; -fx-padding: 15; -fx-background-radius: 8; -fx-border-color: #e9ecef; -fx-border-radius: 8; -fx-border-width: 1;");

        // Stock symbol as title
        Text symbolText = new Text(symbol);
        symbolText.setStyle("-fx-font-size: 16px; -fx-font-weight: bold; -fx-fill: #2c3e50;");

        // Description
        Text descText = new Text("Recommended based on your risk profile");
        descText.setStyle("-fx-font-size: 12px; -fx-fill: #6c757d;");

        // Small line chart
        NumberAxis xAxis = new NumberAxis();
        NumberAxis yAxis = new NumberAxis();
        xAxis.setVisible(false);
        yAxis.setVisible(false);

        LineChart<Number, Number> chart = new LineChart<>(xAxis, yAxis);
        chart.setPrefWidth(300);
        chart.setPrefHeight(120);
        chart.setLegendVisible(false);
        chart.setStyle("-fx-background-color: transparent;");

        XYChart.Series<Number, Number> series = new XYChart.Series<>();
        List<Double> prices = investmentService.getHistoricalPrices(symbol, "1M"); // Last month
        if (!prices.isEmpty()) {
            double minPrice = prices.stream().min(Double::compare).orElse(0.0);
            double maxPrice = prices.stream().max(Double::compare).orElse(100.0);
            yAxis.setLowerBound(minPrice * 0.95);
            yAxis.setUpperBound(maxPrice * 1.05);
        }
        for (int i = 0; i < prices.size(); i++) {
            series.getData().add(new XYChart.Data<>(i, prices.get(i)));
        }
        chart.getData().add(series);

        container.getChildren().addAll(symbolText, descText, chart);
        return container;
    }

    @FXML
    private void onAddInvestment() {
        String type = investment_type_choicebox.getValue();
        String amountText = investment_amount_field.getText();

        if (type == null || type.isEmpty()) {
            System.out.println("Please select an investment type.");
            return;
        }

        try {
            double amount = Double.parseDouble(amountText);
            if (amount <= 0) {
                System.out.println("Investment amount must be positive.");
                return;
            }

            Model model = Model.getInstance();
            String owner = model.getLoggedInClient().getPayeeAddress();

            // Create investment with initial value same as invested amount
            model.getDatabaseDriver().createInvestment(owner, type, amount, amount);

            // Clear fields
            investment_amount_field.clear();

            // Refresh list
            loadInvestments();

            System.out.println("Investment added successfully!");

        } catch (NumberFormatException e) {
            System.out.println("Please enter a valid amount.");
        }
    }
}

